import React, {useState} from 'react';
import {Link} from 'react-router-dom'; 
import { Button } from './Button';
import './NavBar.css';
function NavBar() {
  //set the initial state 
  const [click, setClick] = useState(false);
  const handleClick = () => setClick(!click); //the function will reverse the current state when clicked
  const closeMobileMenu = () => setClick(false)
  const [buttonC, setButton] = useState(true);
//
  //show button function 

  const showButton = () =>{
    if(window.innerWidth <= 960 ){
      setButton(false); 
    }else{
      setButton(true);
    }
  };
  window.addEventListener('resize', showButton);
  return (

    <nav className='navbar'>
        <div className='navbar-container'>
            <Link to = "/" className= 'navbar-logo'>
                   Dagmawi Negesse <i className='fab fa-typo3'></i>
                 </Link>

                 <div className='menu-icon' onClick={handleClick}>
                  {/** this when clicked change states */}
                   <i className={click ? 'fas fa-times': 'fas fa-bars'}></i>
                   </div>

                   <ul className= {click ?'nav-menu active': 'nav-menu'}>
                    <li className='nav-item'>
                      <Link to = '/home' className='nav-links' onClick={closeMobileMenu}>
                        Home
                      </Link>
                    </li>

                    {/* <li className='nav-items'>
                      <Link to = './Resume' className='nav-links' onClick={closeMobileMenu}>
                        Resume
                      </Link>
                    </li> */}

                    <li className='nav-item'>
                      <Link to = '/projects' className='nav-links' onClick={closeMobileMenu}>
                        Projects
                      </Link>
                    </li>

                    <li className='nav-item'>
                      <Link to = '/about' className='nav-links' onClick={closeMobileMenu}>
                        About
                      </Link>
                    </li>

                    {/* <li className='nav-items'>
                      <Link to = './Contacts' className='nav-links' onClick={closeMobileMenu}>
                        Contacts
                      </Link>
                    </li> */}

                    <li className='nav-item'>
                      <Link to = '/downloadResume' className='nav-links-mobile' onClick={closeMobileMenu}>
                        Resume <i class="fas fa-download"></i>
                      </Link>
                    </li>


                   </ul>
                   {buttonC && <Button buttonStyle={'btn--outline'} >Resume
                   <i class="fas fa-download"></i>
                   </Button>}



        </div>



    </nav>
  );



}

export default NavBar;
