import React, {useState, useEffect} from 'react';
import {Link} from 'react-router-dom'; 
// import { Button } from './Button';
import './NavBar.css';
import useScroll from './useScroll'; 


function NavBar() {
  //set the initial state 
  const [click, setClick] = useState(false);
  const handleClick = () => setClick(!click); //the function will reverse the current state when clicked
  const closeMobileMenu = () => setClick(false) // add this to different tags so it will close out of the mobile menu
  const [buttonC, setButton] = useState(true);
//
  //show button function 

  const showButton = () =>{
    if(window.innerWidth <= 960 ){
      setButton(false); 
    }else{
      setButton(true);
    }
  };
  //to remove resume button from appearing again when refreshed
  useEffect(() =>{
    showButton();

  }, []);
  window.addEventListener('resize', showButton);
  // const { y, x, scrollDirection } = useScroll();  

  const styles = {
    active: {
      visibility: "visible",
      transition: "all 0.1s"
    },
    hidden: {
      visibility: "hidden",
      transition: "all 0.5s",
      transform: "translateY(-100%)"
    }
  }

  return (
    <nav className='navbar'>
    <div className='navbar-container'>
      <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
        Dagmawi Negesse

      </Link>
      <div className='menu-icon' onClick={handleClick}>
        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
      </div>
      <ul className={click ? 'nav-menu active' : 'nav-menu'}>
        <li className='nav-item'>
          <Link to='/' className='nav-links' onClick={closeMobileMenu}>
            Home
          </Link>
        </li>
        <li className='nav-item'>
          <Link
            to='/Education'
            className='nav-links'
            onClick={closeMobileMenu}
          >
            Education
          </Link>
        </li>
        <li className='nav-item'>
          <Link
            to='/projects'
            className='nav-links'
            onClick={closeMobileMenu}
          >
            Projects
          </Link>
        </li>


      </ul>

    </div>
  </nav>
  );



}

export default NavBar;
